scripts.script:
let data = await this.quicktext.processTag("to");
// use lastname if firstname is not available
if (typeof data["firstname"] == "undefined") {
  data["firstname"] = [];
}
for (let i = 0; i < data["lastname"].length; i++) {
  if (
    typeof data["firstname"][i] == "undefined" ||
    data["firstname"][i] == ""
  ) {
    data["firstname"][i] = data["lastname"][i];
  }
}

if (typeof data["firstname"] != "undefined") {
  if (this.quicktext.variables.length < 1) this.quicktext.variables[0] = ", ";
  return data["firstname"].join(
    this.quicktext.variables[0].replace(/\\n/g, "\n").replace(/\\t/g, "\t"),
  );
}

let platform = await this.quicktext.getTag("input", "平台");
console.log(`平台为：${platform}`);
platform = platform.toLowerCase();
let type;

if (platform === "instagram") {
  type = await this.quicktext.getTag(
    "input",
    "产出类型",
    "select",
    "post;reel",
  );
} else if (platform === "youtube") {
  type = await this.quicktext.getTag(
    "input",
    "产出类型",
    "select",
    "shorts;Integration;dedicated video",
  );
}
// 空值检查
if (!platform) {
  return "No input provided!";
}

output = type; // output 设置为 type

// 转为小写进行比较

switch (platform) {
  case "youtube":
    return type;
  case "instagram":
    return type;
  default:
    output = "tiktok";
    return "TikTok video";
}

let platform = await this.quicktext.getTag("input", "平台");
let type = output;

console.log(type);

let message = `
  <div>## Video Length</div>
  <p>We're flexible, but ideally **[[INPUT=视频时长(分钟)]] min**. This allows enough time for an engaging and
  informative review without being too lengthy for your viewers. <br>
  </p>
`;

type = type.toLowerCase();
platform = platform.toLowerCase();

switch (platform) {
  case "youtube":
    return message;
  case "instagram":
    if (type === "reel") {
      return message;
    }
    return ""; // 如果不是 Reels，则返回空字符串
  default:
    return message;
}
